import DartsBaseGame from './dartsBaseGame';
import Throw from '../throw';

/**
 * Class representing a 501 Game
 *
 * @class Darts501Game
 */
export default class Darts501Game extends DartsBaseGame {

  static defaultOptions = {
    startingPoints: 501,
    doubleIn: false,
    doubleOut: true
  };

  /**
   * Initializes a new instance of the `Darts501Game` class.
   *
   * @constructs Darts501Game
   * @param {Player[]} players An array of players.
   * @param {Object} [options={}] The options object
   *
   * @example
   * // Creating the new instance
   * var game = new Darts501Game([new Player({name: 'Player 1'}), new Player({name: 'Player 2'})]);
   *
   */
  constructor(players, options = {}) {
    let opts = Object.assign({}, Darts501Game.defaultOptions, options);
    super(players, opts);
  }

  /**
   * Determines whether option `doubleIn` is set to `true` or `false`.
   *
   * @returns {boolean} If `true` it will affect game specific logic.
   */
  get doubleIn() {
    return this._options.doubleIn;
  }

  /**
   * Determines whether option `doubleOut` is set to `true` or `false`.
   *
   * @returns {boolean} If `true` it will affect game specific logic.
   */
  get doubleOut() {
    return this._options.doubleOut;
  }

  /**
   * Represents single `throw`.
   *
   * @param {number} num Number between 0 and 20 plus 25 (bull)
   * @param {number} [multiplier=1] Multiplier is between 1 and 3 for all of the numbers but 25 (which has only x2 multiplier).
   * @returns {Throw} `Throw` instance.
   */
  throw(num, multiplier = 1) {
    let currentThrow = super.throw(num, multiplier);

    if(this.currentPlayerTotalPoints === 0) {
      if(this.doubleOut) {
        if(multiplier === 2) {
          this.currentPlayer.winner = true;
        } else {
          this.invalidateRound(this.currentRound);
        }
      } else {
        this.currentPlayer.winner = true;
      }
    }

    if(this.currentPlayerTotalPoints < 0) {
      this.invalidateRound(this.currentRound);
    }

    if(num && this.doubleIn && (this.currentPlayerTotalPoints + num * multiplier) === this.startingPoints && multiplier !== 2) {
      this.invalidateThrow(currentThrow);
    }

    if(this.doubleOut && this.currentPlayerTotalPoints === 1) {
      this.invalidateRound(this.currentRound);
    }

    return currentThrow;

  }

  /**
   * Calculates points for specific round.
   *
   * @param {Round} round Round instance.
   * @returns {number} Points for round.
   */
  getPointsByRound(round) {
    return Array.from(round.throws).reduce((prev, curr) => prev + curr.number * curr.multiplier, 0);

  }

  /**
   * @returns {number} Calculates points for `currentRound`
   */
  get currentRoundPoints() {
    return this.getPointsByRound(this.currentRound);
  }

  /**
   * @returns {number} Calculates total points for `currentPlayer`
   */
  get currentPlayerTotalPoints() {
    return this.getPointsByPlayer(this.currentPlayer);
  }

  /**
   * Calculates total points for specific player.
   *
   * @param {Player} player Instance of the `Player` class.
   * @returns {number} Total points.
   */
  getPointsByPlayer(player) {
    return this.startingPoints - this.throwsByPlayer(player).reduce((prev, curr) => prev + curr.number * curr.multiplier, 0);
  }

  /**
   * @returns {Number} Points that game starts with.
   */
  get startingPoints() {
    return parseInt(this._options.startingPoints, 10);
  }

  /**
   * Calculates step-by-step throws to check-out in the same round.
   *
   * @param {number} points Total number of points left.
   * @param {number} leftThrows Number of throws.
   * @returns {Array} Array of possible throws as string.
   */
  getCheckoutHint(points, leftThrows = 3) {
    let results = [];

    let possibleThrows = [];

    let singles = [];
    let doubles = [];
    let triples = [];

    let num = 20;

    let getPoints = (throwInstance) => {
      return throwInstance.number * throwInstance.multiplier;
    }

    while(num > 0) {
      singles.push(new Throw(num));
      doubles.push(new Throw(num, 2));
      triples.push(new Throw(num, 3));

      num --;
    }

    singles.push(new Throw(25));
    doubles.push(new Throw(25, 2));

    possibleThrows = possibleThrows.concat(singles, doubles, triples);

    for(let double of doubles) {

      if(points === getPoints(double)) {
        results.push([double]);
      }

      if(leftThrows > 1) {
        for(let possibleThrow of possibleThrows) {

          if(getPoints(possibleThrow) + getPoints(double) === points) {
            results.push([possibleThrow, double]);
          }

          if(leftThrows > 2) {

            for(let possibleThrow2 of possibleThrows) {
              if (getPoints(possibleThrow) + getPoints(possibleThrow2) + getPoints(double) === points) {
                results.push([possibleThrow2, possibleThrow, double]);
              }
            }
          }
        }
      }
    }

    return results;
  }

}

import Round from '../round';

/**
 * Provides all the logic to create multiplier games based on this class
 *
 * @class DartsBaseGame
 */
export default class DartsBaseGame {

  /**
   * @static
   * @property {Object} defaultOptions
   * @property {Boolean} defaultOptions.isComputer
   */
  static defaultOptions = {
    round: {
      throwsPerRound: 3
    }
  };

  _players = new Set();
  _playerRounds = new Map();

  _currentRoundNumber = 0;
  _currentPlayer = null;

  /**
   * Initializes a new instance of the `DartsBaseGame` class.
   *
   * @param {Player[]} players Collection of players.
   * @param {Object} [options={}] Options.
   */
  constructor(players, options = {}) {
    if(!players || !players.length) {
      throw Error('No players provided');
    }

    this.addPlayers(players);
    this._options = Object.assign({}, DartsBaseGame.defaultOptions, options);

    this._playersIterator = this._players[Symbol.iterator]();
    let data = this._playersIterator.next();
    this._currentPlayer = data.value;
    this.nextRound();
  }

  /**
   * Represents single `throw`. Delegates its execution to the `currentRound` object.
   *
   * @param {number} num Number between 0 and 20 plus 25 (bull)
   * @param {number} [multiplier=1] Multiplier is between 1 and 3 for all of the numbers but 25 (which has only x2 multiplier).
   * @returns {Throw} `Throw` instance.
   */
  throw(num, multiplier = 1) {
    return this.currentRound.throw(num, multiplier);
  }

  /**
   * Invalidates specific round.
   *
   * @param {Round} round `Round` instance.
   */
  invalidateRound(round) {
    round.invalidate();
  }

  /**
   * Invalidates specific `throw`.
   *
   * @param {Throw} dartsThrow `Throw` instance.
   */
  invalidateThrow(dartsThrow) {
    dartsThrow.number = 0;
    dartsThrow.multiplier = 1;
  }

  /**
   * Sets `currentPlayer` to the next one.
   * @returns {Player} Returns player.
   */
  nextPlayer() {
    let data = this._playersIterator.next();

    if(data.done) {
      this._playersIterator = this._players[Symbol.iterator]();
      this.nextRound();
      return this.nextPlayer();
    }

    this._currentPlayer = data.value;
    return this._currentPlayer;
  }

  /**
   * Creates new instance of the `Round` for each player.
   */
  nextRound() {
    let round;

    for(let player of this._players) {
      round = new Round(this._options.round);
      this._playerRounds.get(player).get('rounds').add(round);
      this._playerRounds.get(player).set('currentRound', round);
    }

    this._currentRoundNumber++;
  }

  /**
   * Adds a player to the game.
   *
   * @param {Player} player Instance of `Player` class.
   */
  addPlayer(player) {
    this._players.add(player);
    this._playerRounds.set(player, new Map().set('rounds', new Set()));
  }

  /**
   * Add multiple players to the game.
   *
   * @param {Player[]} players Array of players.
   */
  addPlayers(players) {
    for(let player of players) {
      this.addPlayer(player);
    }
  }

  /**
   * @returns {Player[]} Array of players.
   */
  get players() {
    return Array.from(this._players);
  }

  /**
   * @returns {number} Throws per round.
   */
  get throwsPerRound() {
    return this._options.round.throwsPerRound;
  }

  /**
   * @returns {Player} Return `Player`
   */
  get currentPlayer() {
    return this._currentPlayer;
  }

  /**
   * @returns {Round} Return `Round`.
   */
  get currentRound() {
    return this._playerRounds.get(this.currentPlayer).get('currentRound');
  }

  /**
   * @returns {number} Round number.
   */
  get currentRoundNumber() {
    return this._currentRoundNumber;
  }

  /**
   * @param {Player} player Instance of the `Player`.
   * @returns {Round[]} Array of rounds.
   */
  roundsByPlayer(player) {
    return Array.from(this._playerRounds.get(player).get('rounds'));
  }

  /**
   * @param {Player} player Instance of the `Player`.
   * @returns {Throw[]} Array of `Throw` instances.
   */
  throwsByPlayer(player) {
    return this.roundsByPlayer(player).reduce((prev, curr) => {
      return prev.concat(Array.from(curr.throws));
    }, []);
  }

}

import Player from './player';
import Darts501Game from './games/501';

export default {
  Player,
  games: {
    Darts501Game
  }
};

/**
 * Provides all the logic to create instance of Player
 *
 * @class Player
 */
export default class Player {

  /**
   * @property {Object} defaultOptions
   * @property {Boolean} defaultOptions.isComputer
   */
  defaultOptions = {
    isComputer: false
  };

  /**
   * Initializes a new instance of the `Player` class.
   *
   * @constructs Player
   * @param {Object|*} user A user object.
   * @param {Object} [options={}] Options.
   *
   * @example
   *
   * Example 1
   *
   * // Creating a new instance
   * var player1 = new Player({name: 'Player 1'});
   *
   * Example 2
   *
   * // You can pass any object that has defined toString() method:
   * var playerObj = {
   *   name: 'Ben',
   *   toString() { return this.name; }
   * };
   *
   * var player2 = new Player(playerObj);
   *
   */
  constructor(user, options = {}) {
    /**
     * @type {Object|*}
     * @private
     */
    this._user = user;

    /**
     * @type {Object}
     * @private
     */
    this._options = Object.assign({}, this.defaultOptions, options);
  }

  /**
   * @returns {Boolean} Getter
   */
  get isComputer() {
    return this._options.isComputer;
  }

  /**
   * @param {Boolean} bool Setter
   */
  set isComputer(bool) {
    this._options.isComputer = bool;
  }

  /**
   * Returns string representation of Player instance
   * 
   * @returns {string} String representation
   */
  toString() {
    return this._user.toString();
  }

}

